        private View _rootLayout;
        private const string TAG = "VixApp";

        protected override void OnCreate()
        {
            base.OnCreate();
            Initialize();
        }

        private void Initialize()
        {
            Window window = Window.Instance;
            window.BackgroundColor = Color.Black;

            window.KeyEvent += OnKeyEvent;
            _rootLayout = new View
            {
                WidthResizePolicy = ResizePolicyType.FillToParent,
                HeightResizePolicy = ResizePolicyType.FillToParent
            };
            window.Add(_rootLayout);

            //carousel container of thumbnails
            sectionContainer = new View
            {
                WidthResizePolicy = ResizePolicyType.FillToParent,
                HeightResizePolicy = ResizePolicyType.FitToChildren,
                Layout = new LinearLayout
                {
                    LinearOrientation = LinearLayout.Orientation.Vertical,
                    CellPadding = new Size2D(0, 40)
                },
                Focusable = true,
                Sensitive = true,
                Position = new Position(0, 950)
            };

            // 1. ÁREA DE CONTENIDO (Fondo)
            _contentArea = new View
            {
                BackgroundColor = new Color(0.1f, 0.1f, 0.1f, 1.0f),
                WidthResizePolicy = ResizePolicyType.FillToParent,
                HeightResizePolicy = ResizePolicyType.FillToParent,
                Padding = new Extents(120, 0, 0, 0), // Margen para el menú colapsado
                Focusable = true,
                Sensitive = true
            };

            // HOVER CONTENIDO: Contraer sidebar al salir de él
            _contentArea.HoverEvent += (s, e) =>
            {
                if (e.Hover.GetState(0) == PointStateType.Started)
                {
                    _sidebar.AnimateSidebar(false);
                    return true;
                }
                return false;
            };

            _contentArea.KeyEvent += (s, e) =>
            {
                if (e.Key.State == Key.StateType.Down && e.Key.KeyPressedName == "Left")
                {
                    Program._carouselRoots.Clear();
                    _sidebar.RestoreFocus();
                    return true;
                }
                return false;
            };
            _rootLayout.Add(_contentArea);

            // 2. SIDEBAR (Al frente)
            _sidebar = new SidebarView();

            _contentArea.FocusGained += (s, e) =>
            {
                if (_contentArea.Children.Any())
                    FocusManager.Instance.SetCurrentFocusView(_contentArea.Children[0]);
            };

            _sidebar.MenuItemSelected += (id) =>
            {
                LoadPageContent(id);
            };

            _sidebar.RequestCollapse += (s, e) => { FocusManager.Instance.SetCurrentFocusView(_contentArea); };

            _rootLayout.Add(_sidebar);

            // <--- CAMBIO PRINCIPAL: Cargar el menú dinámicamente
            LoadSidebarMenuItems();
            // Carga inicial de contenido (la página "Inicio" o "sports" por defecto)
            LoadPageContent("/ondemand"); // <--- CAMBIO: Cargar la página de inicio por defecto

            _sidebar.RestoreFocus();
        }

        public static void ScrollToViewInNovelSection(View target)
        {
            if (target == null || sectionContainer == null)
                return;

            // posición Y del child dentro del container
            float targetY = target.PositionY;

            var animation = new Animation(300);
            animation.AnimateTo(
                sectionContainer,
                "PositionY",
                -targetY + 800, // margen superior
                new AlphaFunction(AlphaFunction.BuiltinFunctions.EaseInOut)
            );
            animation.Play();
        }

        public static void ScrollToTheBeginPosition(View target)
        {
            if (target == null || sectionContainer == null)
                return;

            // posición Y del child dentro del container
            float targetY = target.PositionY;

            var animation = new Animation(300);
            animation.AnimateTo(
                sectionContainer,
                "PositionY",
                1000, // margen superior
                new AlphaFunction(AlphaFunction.BuiltinFunctions.EaseInOut)
            );
            animation.Play();
        }

        private void ClearContentArea()
        {
            while (sectionContainer.ChildCount > 0)
            {
                var child2 = sectionContainer.GetChildAt(0);
                sectionContainer.Remove(child2);
            }

            while (_contentArea.ChildCount > 0)
            {
                var child = _contentArea.GetChildAt(0);
                _contentArea.Remove(child);
            }
        }

        public void OnKeyEvent(object sender, Window.KeyEventArgs e)
        {
            if (e.Key.State == Key.StateType.Down)
            {
                if (e.Key.KeyPressedName == "XF86Back" || e.Key.KeyPressedName == "Escape")
                {
                    Exit();
                }
            }
        }
        private void LoadPageContent(string pageId)
        {
            ClearContentArea();
            //agrego json de los carruseles de la seccion
            string json = GetPageJson(pageId);
            _contentTitle = new TextLabel
            {
                Text = "VI TEST",
                TextColor = Color.White,
                PointSize = 35,
                HorizontalAlignment = HorizontalAlignment.Center,
                WidthResizePolicy = ResizePolicyType.FillToParent,
                HeightResizePolicy = ResizePolicyType.FillToParent
            };

            if (pageId == "novelas_item")
            {
                _novelPageData = System.Text.Json.JsonSerializer.Deserialize<Root>(
                    json,
                    new JsonSerializerOptions
                    {
                        PropertyNameCaseInsensitive = true
                    });

                FilmsHeroCarousel.StartAutoPlay();
                var heroAmounts = _novelPageData.data.uiPage.uiModules.edges.Where(h => h.node.moduleType == "HERO_CAROUSEL").FirstOrDefault();
                //if (heroCarousel == null)
                //{
                heroCarousel = UiModuleBuilder.Build(heroAmounts.node, "HERO_CAROUSEL");
                _contentArea.Add(heroCarousel);
                //}
                var thumbnailsAmounts = _novelPageData.data.uiPage.uiModules.edges.Where(h => h.node.moduleType == "VIDEO_CAROUSEL").ToList();
                int thumbnailPosition = 0;
                foreach (var item in thumbnailsAmounts)
                {
                    if (_carouselRoots.Count != thumbnailsAmounts.Count)
                    {
                        thumbnails = UiModuleBuilder.Build(item.node, "VIDEO_CAROUSEL", $"{thumbnailPosition}");
                        Program.ScrollToTheBeginPosition(thumbnails);
                        sectionContainer.Add(thumbnails);
                        _carouselRoots.Add(thumbnails);
                        if (thumbnailPosition != 0)
                        {
                            Program._carouselRoots[thumbnailPosition].Opacity = 0f;
                        }
                        thumbnailPosition++;
                    }
                }
                _contentArea.Add(sectionContainer);
                //input del control remoto
                FilmsHeroCarousel.Events();
                FilmsThumbnailsCarousel.Events();
                //le doy foco cada vez que entra al primer thumbnail
                sectionContainer.FocusGained += (s, e) =>
                {
                    if (_contentArea.Children.Any())
                        FocusManager.Instance.SetCurrentFocusView(sectionContainer.Children[0]);
                };
            }
            else
            {
                Program._carouselRoots.Clear();
                _contentTitle.Text = "Sección: " + pageId.ToUpper();
                _contentArea.Add(_contentTitle);
            }
        }
