                        // Filtramos por los tipos de menú que queremos mostrar en el Sidebar principal.
                        if (navEntry.MenuType == "CTV_APP_SETTINGS" || navEntry.MenuType == "CTV_APP_STICKY")
                        {
                            if (navEntry.Sections != null)
                            {
                                foreach (var section in navEntry.Sections)
                                {
                                    if (section.Items != null)
                                    {
                                        menuItems.AddRange(section.Items);
                                    }
                                }
                            }
                        }
                    }
                }
                _sidebar.LoadMenuItems(menuItems); // Llama al método del sidebar para cargar los ítems
            }
            catch (Exception ex)
            {
                Log.Error(TAG, $"Error cargando menú desde JSON: {ex.Message}");
            }
        }

        static void Main(string[] args)
        {
            new Program().Run(args);
        }
    }
}